mapper.select:
SELECT * FROM ( SELECT klc.center_name as center_name, klc.address as address, klc.service_type as service_type, klc.x_coordinate as x_coordinate, klc.y_coordinate as y_coordinate FROM kids_local_center klc WHERE 1=1
UNION ALL
SELECT kbc.center_name as center_name, kbc.address as address, kbc.service_type as service_type, kbc.x_coordinate as x_coordinate, kbc.y_coordinate as y_coordinate FROM kids_bring_center kbc WHERE 1=1
) uni ORDER BY SUBSTRING(address, 1, 8), x_coordinate LIMIT #{start}, #{end}
