mapper.select:
SELECT * FROM hosp_info WHERE ( 6371 * acos( cos(radians(#{lat})) * cos(radians(hosp_lat)) * cos(radians(hosp_lon) - radians(#{lon})) + sin(radians(#{lat})) * sin(radians(hosp_lat)) ) ) <= #{radius}
AND hosp_name LIKE CONCAT('%', #{keyword}, '%')
AND ( hosp_name LIKE CONCAT('%', #{subject}, '%') or hosp_sbj LIKE CONCAT(#{subject}, ',%') )
